(window.webpackJsonp=window.webpackJsonp||[]).push([[34],{385:function(v,_,l){"use strict";l.r(_);var i=l(5),t=Object(i.a)({},function(){var v=this,_=v.$createElement,l=v._self._c||_;return l("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[l("p",[v._v("我们学习一门外语，最常用词汇也得有三、五千个，加上复杂语法，以及同一意思的不同表达方式，成语、俚语，要学会能听会说一门简单外语，即使要到达入门程度，也得花很多时间背单词，听外文资料，培养语感，要学好非得经年累月之功。")]),v._v(" "),l("p",[v._v("计算机编程语言和人类自然语言相比实在是简单太多了。首先一门编程语言词汇（关键字）有限，一般都在几十个，不超过100个。其次在编程语言中，基本上没有模棱两可的语义分歧。而且所有语言要解决的语言层面的问题都差不多，无非是定义一些变量，进行一些流程（顺序、分支、循环），执行一些运算（加减乘除、位、逻辑、字符串等）。计算机还是特别有耐心的一个倾听者和执行者，不管你告诉它多么不合常理，甚至匪夷所思，它从来不会回嘴，只是默默按你说的去做。一句话概括：你可以告诉它很多次，它也不会厌烦，至于它有时反馈不对，那绝对是你的问题（没有告诉它正确的做法）。")]),v._v(" "),l("p",[v._v("按理，学一门计算机编程语言应该很简单，为什么很多人还是觉得难以掌握？")]),v._v(" "),l("ol",[l("li",[v._v("编程语言简单，提供或配套的库是复杂的。编程语言是为相对通用目的实现的，而各语言库是为解决特定问题存在的。每个库都有它要解决的具体问题，更关键的是每个库都有它的使用约束。现代编程语言强大之处在于有很大一部分是系统库或者第三方库提供了各种各样功能。要用好这些库不简单。")]),v._v(" "),l("li",[v._v("每个编程语言都有它要解决的独特问题，或者说每门语言都有它的独特特性。通用的东西，我们学一次就够了，不同的特性，却非得挨个学习才能掌握。比如如果你从自己手工管理内存的语言如c、c++转到具有垃圾自动回收功能的语言如java，你一定要重点了解垃圾回收功能对程序设计实现的影响，更不能认为从此自己就不需要考虑内存管理问题了。")]),v._v(" "),l("li",[v._v("缺乏应用场景。就像你学了一段时间外语，但没有和别人交流的机会，过个一年半载可能就忘得差不多了。")]),v._v(" "),l("li",[v._v("基础原理不够扎实。 所有高级编程语言都是跑在计算机上的，如果对计算机组成和原理不甚清楚，对操作系统没有基本认知，对基础数学、数据结构、网络等知识不甚了然，掌握编程语言就变成空中楼阁。")]),v._v(" "),l("li",[v._v("英语不够好。 目前为止，在计算机领域基本上都是外国人在主导，日常我们使用的大量软件，基本上全部来自美国，不管是免费开源的Linux、Java、Hadoop、PHP、Tomcat、、Spring，还是商业收费的 Windows、WebLogic、Oracle。")])]),v._v(" "),l("p",[v._v("总之：大到编程语言、操作系统、数据库，小到编程框架、日志组件，几乎全部来自美国，三大领域：操作系统、数据库、编程语言中的软件基本都是西方国家的外国人在主导。这些产品的技术文档都是英文的，基本上翻译过的文档都不够原汁原味，甚至错误百出。师夷长技以制夷，为了学习这些技术，不但要学好英语，还要学习他们的思维方式。我认识的很多软件工程师，找技术资料首先找百度上中文的资料，殊不料，这正是舍本逐末的方式，找二手资料，有很多个人理解偏差，也容易有过时的问题。有时间还是学着看英文技术文档，毕竟这也是核心竞争力之一。")]),v._v(" "),l("p",[v._v("总有人问如何学习一门计算机编程语言，这里且厚脸皮表达下个人浅见：")]),v._v(" "),l("ol",[l("li",[v._v("了解该编程语言的总体情况：产生历史，特性（可以做什么，适合场景），谁在用，该语言程序员薪资水平等")]),v._v(" "),l("li",[v._v("关键字和语法，这一步最简单，不用作任何说明。")]),v._v(" "),l("li",[v._v("对比学习，找出不用语言的共同点和不同点，或者是找出已掌握语言和正在学习语言的共同点和不同点，特别是该语言典型的编程范式：命令式编程、申明式编程、函数式编程、响应式编程等")]),v._v(" "),l("li",[v._v("典型例子, 针对特性学习，如go高并发、分布式，如python的科学计算框架")]),v._v(" "),l("li",[v._v("注重语言表象之下的原理，或者说所有语言都通用的内功：数据结构，性能，设计原则和模式，软件工程，解决问题的思路等")]),v._v(" "),l("li",[v._v("思维方式从“面向工具学习”转变成“面向原理学习”，甚至“面向思想学习”，因为表面的东西如工具总是千变万化，而内里如原理和思想却相对稳定。抓住主要矛盾才能事半功倍，在瞬息万变的技术变革潮流中保持清醒的学习头脑，迎头赶上，做时代弄潮儿。")]),v._v(" "),l("li",[v._v("在小项目中学习或在工作中直接使用。一个完整的项目（即使是小游戏，小项目）总是会遇到意向不到的问题，只有面对并解决这些问题，才会快速成长。")]),v._v(" "),l("li",[v._v("找一个能答疑解惑的师傅。在你遇到问题的时候（即使是很小的一个问题），如果没有人指导，可能会一直卡壳，甚至失去继续学习的自信心。在任何学习体验中，好的老师总是事半功倍。")])]),v._v(" "),l("p",[v._v("如果你在学习新编程语言的过程中，遇到任何问题，或者有别的心得体会，欢迎留言交流。")])])},[],!1,null,null,null);_.default=t.exports}}]);